        uint192 value; 
        uint32 betHash; 
        uint32 blockNum; 
    }
    mapping (address => Bet) bets;

    uint public walletBalance = 0; 


    uint public investStart = 1; 
    uint public investBalance = 0; 
    uint public investBalanceMax = 200000 ether; 
    uint public dividendPeriod = 1;
    uint[] public dividends; 


