    onlyRealPeople()
    public 
    {
        openToPublic = false;
        owner = msg.sender;
        whale = whaleAddress;
        totalDonated = 0;
        betLimit = wagerLimit;

    }



    function OpenToThePublic() 
    onlyOwner()
    public
    {
        openToPublic = true;
    }


